const timezone = $json.timezone || 'UTC';
const now = new Date();
const localTime = new Intl.DateTimeFormat('en-US', { timeZone: timezone, hour12: false, hour: '2-digit', minute: '2-digit' }).format(now);
const localHour = parseInt(localTime.split(':')[0]);

let staticData = this.getWorkflowStaticData('global');
let streak = staticData.gymStreak || 0;
if (localHour >= 6 && localHour < 18) { streak += 1; staticData.gymStreak = streak; }

const forecast = $json.forecast || [];
const next12 = forecast.slice(0, 4);

const current = forecast[0] || {};
const currentMain = current.weather ? current.weather[0].main : 'N/A';
const currentTemp = current.main ? Math.round(current.main.temp) : 'N/A';
const weatherEmojiMap = { Clear:'☀️', Clouds:'☁️', Rain:'🌧', Drizzle:'🌦', Thunderstorm:'⛈', Snow:'❄️', Mist:'🌫', Fog:'🌫' };
const currentEmoji = weatherEmojiMap[currentMain] || '🌤';

const rainProbabilities = next12.map(f => f.pop != null ? Math.round(f.pop*100) : 0);
const rainEmojis = rainProbabilities.map(p => { if(p===0) return '☀️'; if(p<=30) return '🌤'; if(p<=60) return '⛅️'; if(p<=80) return '🌧'; return '⛈'; });
const forecastText = next12.map((f,i) => { const timeLabel = new Date(f.dt*1000).toLocaleTimeString('en-US',{timeZone: timezone,hour:'2-digit',minute:'2-digit'}); return `${timeLabel} ${rainEmojis[i]} ${rainProbabilities[i]}%`; }).join('  |  ');

let gymText = streak===0 ? '🏁 Let\'s get started with your first gym day!' : `🏋️‍♂️ You\'re on a ${streak}-day gym streak!`;
let greeting = localHour<12?'Good morning!':localHour<18?'Good afternoon!':'Good evening!';

let text = `${greeting} | ${localTime}\n\nCurrent: ${currentEmoji} ${currentMain}, ${currentTemp}°C\n\nNext 12h:\n${forecastText}\n\n${gymText}`;

return [{ sendNotification:true, text }];
